import React, { useState } from 'react';
import { connect } from 'react-redux';
import { submitCreator } from '../redux/React-Form/form-action';
import StudentInfor from './StudentInfor';

const StudentDetails = (props) => {
  const [state, setState] = useState({
    Id: '',
    Name: '',
    Phone: '',
    Email: '',
    submitted: false
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setState((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const action = submitCreator(state);
    props.dispatch(action);
    setState({
      Id: '',
      Name: '',
      Phone: '',
      Email: '',
      submitted: true
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h1 className="bg-dark text-light">Thông tin sinh viên</h1>
        <div className="form-row">
          <div className="form-group col-md-6">
            <label>Mã SV</label>
            <input
              onChange={handleChange}
              value={state.Id}
              name="Id"
              type="id"
              className="form-control"
            />
          </div>
          <div className="form-group col-md-6">
            <label>Tên SV</label>
            <input
              onChange={handleChange}
              value={state.Name}
              name="Name"
              type="text"
              className="form-control"
            />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group col-md-6">
            <label>Số điện thoại</label>
            <input
              onChange={handleChange}
              value={state.Phone}
              name="Phone"
              type="text"
              className="form-control"
            />
          </div>
          <div className="form-group col-md-6">
            <label>Email</label>
            <input
              onChange={handleChange}
              value={state.Email}
              name="Email"
              type="email"
              className="form-control"
            />
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          Thêm sinh viên
        </button>
      </form>
      {state.submitted && <StudentInfor student={state} />}
    </div>
  );
};

export default connect()(StudentDetails);
