import { combineReducers, createStore } from "redux";

/**
 * Reducer: nơi quản lý state, và logic
 * state = 10; 10 giá trị default của state
 * reducer bắt buộc có return;
 *
 * khi dispatch thì nó sẽ chạy tất cả các hàm reducer của các bạn.
 * Chúng ta muốn xử lý xét lại state nào thì vào reducer đó xử lý.
 */

// Định nghĩa reducer cho listStudent
const listStudentReducer = (state = [], action) => {
  switch (action.type) {
    // Xử lý các action liên quan đến listStudent
    default:
      return state;
  }
};

// Kết hợp các reducer trong rootReducer
const rootReducer = combineReducers({
  listStudent: listStudentReducer,
});

export const store = createStore(
  rootReducer,
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);
